<?phpclass Dashboardmodel extends ci_model{    		public function getregion_by_ip($compaign='')	{			$this->db->select('beat_name');		$this->db->from('login_detail');			$this->db->where('EmailID',$compaign);		$this->db->group_by('beat_name');		$query = $this->db->get();		//echo "<pre>"; print_r($this->db->last_query()); die;		return $query->result_array();	}	}?>